INSERT INTO `reading_classify` VALUES(NULL,"外文原版","http://127.0.0.1:9000/img/class_img/10.jpg","翻译并不能完全填平语言之间的鸿沟，不同的语感，语言后面深厚的文化内涵，即使再“信达雅”的翻译，也无法百分百传递。我喜欢读外文原版作品，就是希望享受这种原汁原味的阅读快感。","曾小墨","http://127.0.0.1:9000/img/pic/23.jpg");
